import React, { useState, useEffect } from 'react';

function Blog() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch posts from the backend server when the component mounts
        fetch('/api/posts')
            .then(response => response.json())
            .then(data => {
                setPosts(data);
                setLoading(false); // Update loading state once fetch is complete
            })
            .catch(error => {
                console.error('Error fetching posts:', error);
                setLoading(false); // Update loading state in case of error
            });
    }, []); // Empty dependency array to run effect only once on mount

    if (loading) {
        return <p>Loading...</p>; // Display loading message while fetching posts
    }

    return (
        <div>
            <h2>Blog Posts</h2>
            {posts.map((post) => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.content}</p>
                </div>
            ))}
        </div>
    );
}

export default Blog;
