import React from 'react';

function NewPost({ onAddPost }) {
    const handleSubmit = (e) => {
        e.preventDefault();
        // Display a message as this component does not modify the server
        alert('New post cannot be added from this component. Modify server.js to handle post addition.');
    };

    return (
        <div>
            <h2>Add a New Post</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" readOnly value="New post title" />
                </div>
                <div>
                    <label>Content:</label>
                    <textarea readOnly>Enter post content here</textarea>
                </div>
                <button type="submit">Add Post</button>
            </form>
        </div>
    );
}

export default NewPost;
