import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests

function Blog() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Fetch data from the server when the component mounts
        const fetchData = async () => {
            try {
                const response = await axios.get('/api/posts'); // Assuming your server is running locally on the same port
                setPosts(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData(); // Call the fetchData function
    }, []); // The empty array [] as the second argument ensures this effect runs only once, similar to componentDidMount

    return (
        <div>
            <h2>Blog Posts</h2>
            {posts.map((post) => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.content}</p>
                </div>
            ))}
        </div>
    );
}

export default Blog;
